	let button = $C('toolbarbutton', {
		id: "ReStart-button",
		class: 'toolbarbutton-1 chromeclass-toolbar-additional',
		label: "重新啟動",
		tooltiptext: "左鍵：重新啟動並清除緩存\n中鍵：重新啟動但停用附加元件\n右鍵：重新啟動",
		removable: "true",
		overflows: "false",
		type: "button",
		style: "list-style-image: url(data:image/png;base64,)",
		context: '_child',
		onclick: 'restart.onClick(event);',
	});
	ToolbarManager.addWidget(window, button, true);
	
	restart = {
		onClick: function(event) {
			switch (event.button) {
				case 0:
					Services.appinfo.invalidateCachesOnRestart() || Application.restart();
					break;
				case 1:
					safeModeRestart();
					break;
				case 2:
					Application.restart();
					break;
			}
		}
	}
	
